def translate(lang, strs):
    payload = {
        "sourceLanguage": "en",
        "targetLanguages": [lang],
        "units": []
    }

    for i, s in enumerate(strs):
        if len(s) == 2:
            payload['units'] += [{'value': s[0], 'key': f'{i}.0'}, {'value': s[1], 'key': f'{i}.1'}]
        else:
            payload['units'] += [{'value': '', 'key': f'{i}.0'}, {'value': '', 'key': f'{i}.1'}]

    r = requests.post('https://sandbox.api.sap.com/mlfs/api/v2/text/translation', headers={
                      'APIKey': '9wFfHLfosRCGX7Z3DkpaG4fxKtMhj7GM', "Content-Type": 'application/json'}, json=payload)
    op = r.json()
    # print(r.request.body)
    # print('op: ', op)

    result = []

    for u in op['units']:
        key = list(map(int,u['key'].split('.')))
        if key[1] == 0:
            result.append([])
        result[key[0]].append(u['translations'][0]['value'])

    return result


